document.addEventListener("DOMContentLoaded", function() {
    let slides = document.querySelectorAll('.slider-item');
    let currentIndex = 0;
    const totalSlides = slides.length;

    function showSlide(index) {
        slides.forEach((slide, i) => {
            slide.style.display = i === index ? 'block' : 'none';
            slide.style.transform = 'scale(1.1)';
            slide.style.filter = 'blur(4px)';
            slide.style.transition = 'transform 0.5s ease, opacity 0.5s ease, filter 0.5s ease';
            slide.style.display = 'none';
        });
        const currentSlide = slides[index];
        currentSlide.style.display = 'block';
        setTimeout(() => {
            currentSlide.style.opacity = '1';
            currentSlide.style.transform = 'scale(1.1)';
            currentSlide.style.filter = 'blur(0)';
        }, 50);
    }

    function nextSlide() {
        currentIndex = (currentIndex + 1) % totalSlides;
        showSlide(currentIndex);
    }

    showSlide(currentIndex);
    setInterval(nextSlide, 10000); // Change slide every 10 seconds
});

//  gallery-item

document.addEventListener("DOMContentLoaded", function() {
    const slides = document.querySelectorAll('.gallery-item');
    let currentIndex = 0;
    const totalSlides = slides.length;
    const intervalTime = 5000;
    let slideInterval;

    function showSlide(index) {
        slides.forEach((slide, i) => {
            slide.style.opacity = '0';
            slide.style.transform = 'scale(1)';
            slide.style.filter = 'blur(5px)';
            slide.style.transition = 'transform 1.5s ease, opacity 0.5s ease, filter 0.5s ease';
            slide.style.display = 'none';
        });

        const currentSlide = slides[index];
        currentSlide.style.display = 'block';
        setTimeout(() => {
            currentSlide.style.opacity = '1';
            currentSlide.style.transform = 'scale(1.1)';
            currentSlide.style.filter = 'blur(0)';
        }, 50);
    }

    function nextSlide() {
        currentIndex = (currentIndex + 1) % totalSlides;
        showSlide(currentIndex);
    }

    function prevSlide() {
        currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
        showSlide(currentIndex);
    }

    function startSlideshow() {
        slideInterval = setInterval(nextSlide, intervalTime);
    }

    function stopSlideshow() {
        clearInterval(slideInterval);
    }

    document.querySelector('.slider-nav').innerHTML = `
        <button class="prev-button">Previous</button>
        <button class="next-button">Next</button>
    `;

    document.querySelector('.next-button').addEventListener('click', function() {
        stopSlideshow();
        nextSlide();
        startSlideshow();
    });

    document.querySelector('.prev-button').addEventListener('click', function() {
        stopSlideshow();
        prevSlide();
        startSlideshow();
    });

    document.querySelector('.gallery-area').style.backgroundColor = 'rgba(0, 0, 0, 0.8)'; // Matte black background

    showSlide(currentIndex);
    startSlideshow();
});


